mapper.select:
SELECT part.partname, def.path, NVL(ROUND(AVG((finisheddate-starteddate)+1),0),0) AS workingDayAVG, NVL(MIN(DISTINCT((finisheddate-starteddate)+1)),0) AS workingDayMIN, NVL(MAX(DISTINCT((finisheddate-starteddate)+1)),0) AS workingDayMAX, COUNT(1) AS totalCOUNT FROM bpm_procinst inst INNER JOIN (SELECT prodverid, parentfolder, defid, name, SYS_CONNECT_BY_PATH(name,'>') AS path, level, CONNECT_BY_ROOT name AS parentname FROM bpm_procdef WHERE objtype = 'process' AND isdeleted = 0 START WITH parentfolder = -1 CONNECT BY PRIOR defid = parentfolder) def ON inst.defid = def.defid INNER JOIN emptable emp ON emp.empcode = inst.initep AND emp.isdeleted = 0 INNER JOIN parttable part ON part.partcode = emp.partcode AND part.isdeleted = 0 WHERE inst.status = 'Completed' AND inst.isdeleted = 0 AND inst.issubprocess = 0
AND part.partcode = #{partCode}
AND #{searchFromDate} <= TO_CHAR(finisheddate,'YYYY-MM-DD') AND TO_CHAR(finisheddate,'YYYY-MM-DD') <= #{searchToDate}
GROUP BY part.partname, def.path
