mapper.select:
SELECT DISTINCT INST.INSTID AS INSTANCEID, DEFVER.DEFNAME AS PROCESSNAME, INST.INITRSNM, INST.NAME AS INSTANCENAME, TO_CHAR(INST.STARTEDDATE,'YYYY-MM-DD HH24:MI') AS STARTDATE, TO_CHAR(INST.FINISHEDDATE,'YYYY-MM-DD HH24:MI') AS ENDDATE FROM BPM_PROCINST INST INNER JOIN BPM_WORKLIST WL ON INST.INSTID=WL.INSTID INNER JOIN BPM_PROCDEFVER DEFVER ON DEFVER.DEFVERID=INST.DEFVERID INNER JOIN BPM_WORKLIST INITTASK ON INITTASK.TASKID=INST.INITTASKID WHERE 1=1
AND INST.STATUS =#{status}
AND INST.ISDELETED='0' AND WL.STATUS IN ('NEW','CONFIRMED','DRAFT','COMPLETED','DELEGATED') AND WL.ENDPOINT =#{userId}
AND INITTASK.ENDPOINT=#{userId}
ORDER BY STARTDATE DESC
